package week1.day2;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 7;
		
		if (a%2 ==0) {
			System.out.println("The number is even");
		}
		else if(a%2!=0){
			System.out.println("The number is odd");
		}

	}
}
